def solve():
    n, k = map(int, input().split())
    total_days = 2**n

    def calculate_probability(k):
        if k > total_days:
            return 1

        probability = 1
        for i in range(k):
            probability *= (total_days - i) / total_days
        return 1 - probability

    probability = calculate_probability(k)

    numerator = int(probability * (2**18) + 0.5)
    denominator = 2**18

    def gcd(a, b):
        if b == 0:
            return a
        return gcd(b, a % b)

    gcd_value = gcd(numerator, denominator)
    numerator //= gcd_value
    denominator //= gcd_value

    print(numerator % (10**6 + 3), denominator % (10**6 + 3))

