import math

def distance(x1, y1, x2, y2):
    return math.sqrt((x2 - x1)**2 + (y2 - y1)**2)

def is_disjoint(range1, range2):
    x1, y1, r1 = range1
    x2, y2, r2 = range2
    d = distance(x1, y1, x2, y2)
    return d > r1 + r2

def is_subset(range1, range2):
    x1, y1, r1 = range1
    x2, y2, r2 = range2
    d = distance(x1, y1, x2, y2)
    return d <= r1 + r2

def is_covered(range1, range2):
    x1, y1, r1 = range1
    x2, y2, r2 = range2
    d = distance(x1, y1, x2, y2)
    return d <= r1 + r2 and d >= abs(r1 - r2)

