        nonlocal a
        i = len(a) - 1
        while i > 0 and a[i - 1] >= a[i]:
            i -= 1
        if i <= 0:
            return False
        j = len(a) - 1
        while a[j] <= a[i - 1]:
            j -= 1
        a[i - 1], a[j] = a[j], a[i - 1]
        a[i:] = a[i:][::-1]
        next_permutation.append(a[:])
