        gcd_val = gcd(abs(int(ratio)), abs(int(1/ratio)))
        ratio = int(ratio / gcd_val) * int(1/ratio)
        if ratio > 0:
            print(int(dx), int(dy), ratio)
        else:
            print(int(dx), int(dy), -ratio)

