def solve():
    s = input()
    w = input()

    left = s[:s.index("|")]
    right = s[s.index("|") + 1:]

    if len(left) == len(right):
        print(s)
        return

    if len(left) > len(right):
        diff = len(left) - len(right)
        for i in range(diff):
            if w[i] in left:
                left = left.replace(w[i], "", 1)
            else:
                right = right + w[i]
    else:
        diff = len(right) - len(left)
        for i in range(diff):
            if w[i] in right:
                right = right.replace(w[i], "", 1)
            else:
                left = left + w[i]

    if len(left) == len(right):
        print(s.replace("|", left + "|" + right))
    else:
        print("Impossible")

solve()

def solve():
    s = input()
    n = len(s)
    pivot_index = s.find('^')
    left_sum = 0
    right_sum = 0

    for i in range(n):
        if s[i] == '=':
            continue
        if s[i] == '^':
            if i < pivot_index:
                right_sum += int(s[i])
            else:
                left_sum += int(s[i])
        else:
            if i < pivot_index:
                left_sum += int(s[i])
            else:
                right_sum += int(s[i])

    if left_sum == right_sum:
        print("balance")
    elif left_sum > right_sum:
        print("left")
    else:
        print("right")

solve()

