        if current_sum == target_sum:
            return 1
        if current_sum > target_sum or remaining_cards == 0:
            return 0

        combinations = 0
        for i in range(1, k + 1):
            combinations += find_combinations(target_sum, current_sum + i, remaining_cards - 1, current_deck + 1)
