        n = len(matrix)
        m = len(matrix[0])
        sub_matrices = []
        for i in range(n - 1):
            for j in range(m - 1):
                sub_matrix = [row[j:j+2] for row in matrix[i:i+2]]
                sub_matrices.append(sub_matrix)
        return sub_matrices

    def count_ones(matrix):
        count = 0
        for row in matrix:
            count += row.count('1')
        return count

    def is_good(matrix):
        sub_matrices = get_sub_matrices(matrix)
        for sub_matrix in sub_matrices:
            if sum(sum(row) for row in sub_matrix) % 2 == 0:
                return False
        return True

