def solve():
    M, S = map(int, input().split())
    preferences = []
    for _ in range(M):
        preferences.append(list(map(int, input().split())))

    def find_common(songs):
        if not songs:
            return []

        common_songs = []
        for song in songs:
            if song not in common_songs:
                common_songs.append(song)

        return common_songs

    def find_preferred(preferences, song):
        for preference in preferences:
            if preference.index(song) < len(preference) - 1:
                return preference[preference.index(song) + 1:]
        return []

    def find_set_list():
        set_list = []
        while True:
            songs_to_play = find_common(set_list)
            if not songs_to_play:
                return set_list

            new_songs = []
            for song in songs_to_play:
                preferred_songs = find_preferred(preferences, song)
                if preferred_songs:
                    new_songs.extend(preferred_songs)

            set_list.extend(new_songs)
            set_list = list(set(set_list))
            set_list.sort()

    set_list = find_set_list()

    print(len(set_list))
    print(*set_list)

