    x_col = -1
    for i in range(n):
        for j in range(m):
            if maze[i][j] == "X":
                x_row = i
                x_col = j

    def is_valid_move(row, col):
        return 0 <= row < n and 0 <= col < m and maze[row][col] != "*"

    def get_neighbors(row, col):
        neighbors = []
        if is_valid_move(row - 1, col):
            neighbors.append("U")
        if is_valid_move(row + 1, col):
            neighbors.append("D")
        if is_valid_move(row, col - 1):
            neighbors.append("L")
        if is_valid_move(row, col + 1):
            neighbors.append("R")
        return neighbors

    def find_cycle(start_row, start_col, path, k):
        if len(path) == k:
            if path[0] == "U" and path[-1] == "D":
                return path
            if path[0] == "D" and path[-1] == "U":
                return path
            if path[0] == "L" and path[-1] == "R":
                return path
            if path[0] == "R" and path[-1] == "L":
                return path
            return None
        for neighbor in get_neighbors(start_row, start_col):
            new_path = path + [neighbor]
            result = find_cycle(start_row, start_col, new_path, k)
            if result is not None:
                return result
        return None

    def find_min_cycle():
        min_cycle = None
        for row in range(n):
            for col in range(m):
                if maze[row][col] == "X":
                    cycle = find_cycle(row, col, [""], k)
                    if cycle is not None:
                        if min_cycle is None or cycle < min_cycle:
                            min_cycle = cycle
        return min_cycle

    min_cycle = find_min_cycle()
    if min_cycle is None:
        print("IMPOSSIBLE")
    else:
        print("".join(min_cycle))

