    n, m = map(int, input().split())
    friendships = []
    for _ in range(m):
        friendships.append(list(map(int, input().split())))

    # Create an adjacency list representation of the graph
    graph = [[] for _ in range(n)]
    for friendship in friendships:
        graph[friendship[0] - 1].append(friendship[1] - 1)
        graph[friendship[1] - 1].append(friendship[0] - 1)

    # Perform DFS to find strongly connected components
    visited = [0] * n
    sccs = []
    def dfs(node, component):
        visited[node] = 1
        component.append(node)
        for neighbor in graph[node]:
            if visited[neighbor] == 0:
                dfs(neighbor, component)
            elif visited[neighbor] == 1:
                # If the neighbor is in a different SCC, then there's a cycle
                # and we can't divide the people into groups
                print(-1)
                return
    for i in range(n):
        if visited[i] == 0:
            component = []
            dfs(i, component)
            sccs.append(component)

    # Find the minimum number of groups needed
    groups_needed = 0
    for scc in sccs:
        if len(scc) > 0:
            groups_needed += 1
        else:
            # If a SCC is empty, it means there's a node with no friends
            # and we can put it in a separate group
            groups_needed += 1

    print(groups_needed)

