    n = int(input())
    segments = []
    for _ in range(n):
        l, r = map(int, input().split())
        segments.append((l, r))

    def intersect(a, b):
        l1, r1 = a
        l2, r2 = b
        if r1 < l2 or r2 < l1:
            return False
        return True

    def is_tree(graph):
        if len(graph) == 1:
            return True

        for node in graph:
            for neighbor in graph[node]:
                if neighbor == node:
                    return False

        for node in graph:
            for neighbor in graph[node]:
                if not intersect((node[0], node[1]), (neighbor[0], neighbor[1])):
                    return False

        return len(graph) == n

    graph = {}
    for i in range(n):
        graph[i] = []
        for j in range(n):
            if i != j and intersect(segments[i], segments[j]):
                graph[i].append(j)

    if is_tree(graph):
        print("YES")
    else:
        print("NO")

