def solve():
    n = int(input())
    x = list(map(int, input().split()))

    tree = [[] for _ in range(n)]
    for _ in range(n - 1):
        a, b = map(int, input().split())
        tree[a - 1].append(b - 1)
        tree[b - 1].append(a - 1)

    def gcd(a, b):
        if b == 0:
            return a
        return gcd(b, a % b)

    def find_gcds(u, v):
        if u == v:
            return x[u]

        gcds = []
        stack = [(u, [x[u]])]
        while stack:
            node, path = stack.pop()
            for neighbor in tree[node]:
                if neighbor == v:
                    gcds.append(gcd(*path))
                else:
                    stack.append((neighbor, path + [x[neighbor]]))

        return gcds

    total_sum = 0
    for i in range(n):
        total_sum += sum(find_gcds(0, i)) % (10**9 + 7)

    print(total_sum % (10**9 + 7))

solve()

