    def is_functional(grid):
        n, m = len(grid), len(grid[0])
        visited = [[False] * m for _ in range(n)]

        def dfs(i, j):
            if i < 0 or i >= n or j < 0 or j >= m or visited[i][j] or grid[i][j] == 'C':
                return False

            visited[i][j] = True
            if i == n - 1 and j == m - 1:
                return True

            # Move right
            if grid[i][j] == 'R':
                if not dfs(i, j + 1):
                    return False

            # Move down
            if grid[i][j] == 'D':
                if not dfs(i + 1, j):
                    return False

            return True

        return dfs(0, 0)

    def change_directions(grid):
        n, m = len(grid), len(grid[0])
        count = 0

        def dfs(i, j):
            nonlocal count
            if i < 0 or i >= n or j < 0 or j >= m or grid[i][j] == 'C':
                return

            count += 1
            grid[i][j] = 'C'

            # Move right
            if grid[i][j] == 'R':
                dfs(i, j + 1)

            # Move down
            if grid[i][j] == 'D':
                dfs(i + 1, j)

        dfs(0, 0)
        return count

    if is_functional(grid):
        print(0)
    else:
        print(change_directions(grid))

