        print(-1)
        return

    def is_valid_move(s, x):
        new_s = str(int(s) + x)
        if new_s[0] == '1':
            return False
        if new_s[n] == '1':
            return False
        return True

    def find_sequence(s, n, m):
        if n == 0:
            if s == '0':
                return ['']
            else:
                return []

        sequence = []
        for i in range(1, m + 1):
            if is_valid_move(s, i):
                new_s = s + str(i)
                new_sequence = find_sequence(new_s, n - 1, m)
                if new_sequence:
                    sequence.append(str(i) + ' ' + ' '.join(new_sequence))
        return sequence

    sequences = find_sequence(s, n, m)

    if not sequences:
        print(-1)
        return

    shortest_sequence = min(sequences, key=lambda x: len(x.split()))

    print(shortest_sequence)

