def solve():
    n, m, p = map(int, input().split())
    edges = []
    for _ in range(m):
        a, b, c = map(int, input().split())
        edges.append((a - 1, b - 1, c))

    dp = {}
    def dfs(u, t, coins):
        if (u, t, coins) in dp:
            return dp[(u, t, coins)]

        if u == n - 1:
            dp[(u, t, coins)] = coins
            return coins

        max_score = -1
        for i in range(len(edges)):
            a, b, c = edges[i]
            if a == u:
                new_score = coins + c
                new_time = t + 1
                if (b, new_time, new_score) in dp:
                    max_score = max(max_score, dp[(b, new_time, new_score)])
                else:
                    max_score = max(max_score, dfs(b, new_time, new_score))

        dp[(u, t, coins)] = max_score
        return max_score

    max_score = dfs(0, 0, 0)
    if max_score == -1:
        print(-1)
    else:
        print(max_score)

