def mergesort(a, l, r):
    if r - l <= 1:
        return

    mid = (l + r) // 2

    mergesort(a, l, mid)
    mergesort(a, mid, r)

    merge(a, l, mid, r)

def merge(a, l, mid, r):
    left = a[l:mid]
    right = a[mid:r]

    i = j = 0
    k = l

    while i < len(left) and j < len(right):
        if left[i] <= right[j]:
            a[k] = left[i]
            i += 1
        else:
            a[k] = right[j]
            j += 1
        k += 1

    while i < len(left):
        a[k] = left[i]
        i += 1
        k += 1

    while j < len(right):
        a[k] = right[j]
        j += 1
        k += 1

