def count_calls(a):
    if len(a) == 1:
        return 1

    mid = len(a) // 2

    left_calls = count_calls(a[:mid])
    right_calls = count_calls(a[mid:])

    if left_calls + right_calls == k:
        return True

    return False

def generate_permutation(n, k):
    if k > n:
        return None

    if k == 1:
        return [1]

    permutation = generate_permutation(n - 1, k - 1)

    if permutation is None:
        return None

    permutation.append(n)

    return permutation

