def solve():
    p, d, n = map(int, input().split())
    grid = [[0] * n for _ in range(n)]
    seeds = []
    for i in range(p):
        r, c = map(int, input().split())
        seeds.append((r, c))

    def is_valid(r, c):
        return 0 <= r < n and 0 <= c < n

    def grow(r, c):
        grid[r][c] = 1
        directions = [(0, 1), (0, -1), (1, 0), (-1, 0)]
        for dr, dc in directions:
            nr, nc = r + dr, c + dc
            if is_valid(nr, nc):
                if grid[nr][nc] == 1:
                    return False
                grid[nr][nc] = 1
                return True
        return False

    def check(r, c):
        grid[r][c] = 2
        directions = [(0, 1), (0, -1), (1, 0), (-1, 0)]
        for dr, dc in directions:
            nr, nc = r + dr, c + dc
            if is_valid(nr, nc):
                if grid[nr][nc] == 1:
                    return False
                if grid[nr][nc] == 2:
                    return True
                grid[nr][nc] = 2
                if not check(nr, nc):
                    return False
        return True

    alive = []
    for r, c in seeds:
        if not grow(r, c):
            alive.append(r * n + c + 1)
        else:
            if check(r, c):
                alive.append(r * n + c + 1)
            else:
                alive.append(0)

    print(*alive)

solve()

