    def is_valid(num1, num2, op):
        if op == '+':
            return num1 + num2 == t
        elif op == '-':
            return num1 - num2 == t
        elif op == '*':
            return num1 * num2 == t
        elif op == '/':
            if num2 == 0:
                return False
            return num1 / num2 == t

    def find_valid_numbers(num1, remaining_numbers):
        valid_numbers = []
        for num2 in remaining_numbers:
            if is_valid(num1, num2, op):
                valid_numbers.append(num2)
        return valid_numbers

    def backtrack(index):
        if index == m:
            return 1
        count = 0
        remaining_numbers = [i for i in range(1, n + 1) if i not in [grid[index][0] - 1, grid[index][1] - 1]]
        for num2 in find_valid_numbers(grid[index][0] - 1, remaining_numbers):
            count += backtrack(index + 1)
        return count

    print(backtrack(0))

solve()

