                count[digit - 1] += 1
            temp_x //= 10
        return count

    def product_digits(x):
        count = 1
        temp_x = x
        while temp_x > 0:
            digit = temp_x % 10
            if digit != 0:
                count *= digit
            temp_x //= 10
        return count

    def get_digit(x):
        count = 1
        temp_x = x
        while temp_x > 0:
            digit = temp_x % 10
            if digit != 0:
                count *= digit
            temp_x //= 10
        return count % 10

    def solve_range(l, r):
        if l == r:
            return [1]

        count = [0] * 10
        temp_l = l
        while temp_l > 0:
            digit = temp_l % 10
            if digit != 0:
                count[digit - 1] += 1
            temp_l //= 10

        temp_r = r
        while temp_r > 0:
            digit = temp_r % 10
            if digit != 0:
                count[digit - 1] -= 1
            temp_r //= 10

        result = []
