            if is_safe(x, y):
                if (x, y) == (W - 1, H - 1):
                    return time
                for dx in [-1, 0, 1]:
                    for dy in [-1, 0, 1]:
                        nx, ny = x + dx, y + dy
                        if 0 <= nx < W and 0 <= ny < H and (nx, ny) not in visited:
