import math

def solve():
    n = int(sys.stdin.readline())
    edges = []
    for _ in range(n - 1):
        a, b = map(int, sys.stdin.readline().split())
        edges.append((a - 1, b - 1))

    def distance(x1, y1, x2, y2):
        return math.sqrt((x2 - x1)**2 + (y2 - y1)**2)

    def find_path(parent, node):
        if node == -1:
            return []
        path = [node]
        find_path(parent, parent[node])
        path.extend(find_path(parent, i) for i in edges if i[1] == node)
        return path

    def position(node, path):
        if node == -1:
            return (0, 0)
        x, y = position(parent[node], path)
        if node == path[0]:
            return (x, y)
        else:
            return (x + 1 if node < path[0] else x - 1, y)

    parent = [-1] * n
    for i in range(n):
        parent[i] = edges[i][0]

    path = find_path(parent, 0)
    positions = [position(i, path) for i in range(n)]

    for x, y in positions:
        sys.stdout.write(f"{x:.12f} {y:.12f}\n")

