def solve():
    n, m, b = map(int, input().split())
    grid = [[0] * m for _ in range(n)]
    blocks = []
    for _ in range(b):
        r, c = map(int, input().split())
        grid[r - 1][c - 1] = 1
        blocks.append((r - 1, c - 1))

    def is_valid_move(r, c):
        if r < 0 or r >= n - 1 or c < 0 or c >= m - 1:
            return False
        return grid[r][c] == 0

    def get_neighbors(r, c):
        neighbors = []
        if r > 0 and is_valid_move(r - 1, c):
            neighbors.append((r - 1, c))
        if r < n - 2 and is_valid_move(r + 1, c):
            neighbors.append((r + 1, c))
        if c > 0 and is_valid_move(r, c - 1):
            neighbors.append((r, c - 1))
        if c < m - 2 and is_valid_move(r, c + 1):
            neighbors.append((r, c + 1))
        return neighbors

    def dfs(r, c, visited):
        if (r, c) in visited:
            return
        visited.add((r, c))
        for nr, nc in get_neighbors(r, c):
            dfs(nr, nc, visited)

    visited = set()
    for block in blocks:
        dfs(block[0], block[1], visited)

    if len(visited) != b:
        print("impossible")
        return

    print("possible")
    moves = []
    for block in blocks:
        r, c = block
        if r == 0:
            moves.append("< " + str(c + 1))
        elif r == n - 1:
            moves.append("^ " + str(c + 1))
        elif c == 0:
            moves.append("> " + str(r + 1))
        else:
            moves.append("v " + str(r + 1))

    print(*moves, sep="\n")

