def solve():
    n = int(input())
    edges = []
    for _ in range(2 * (n - 1)):
        u, v = map(int, input().split())
        edges.append((u, v))

    def find(u, parent):
        if parent[u] == u:
            return u
        parent[u] = find(parent[u], parent)
        return parent[u]

    def union(u, v, parent, rank):
        u_root = find(u, parent)
        v_root = find(v, parent)

        if u_root == v_root:
            return False

        if rank[u_root] < rank[v_root]:
            parent[u_root] = v_root
        elif rank[u_root] > rank[v_root]:
            parent[v_root] = u_root
        else:
            parent[v_root] = u_root
            rank[u_root] += 1

        return True

    parent = list(range(n + 1))
    rank = [0] * (n + 1)

    left_edges = []
    right_edges = []

    for edge in edges:
        if union(edge[0], edge[1], parent, rank):
            left_edges.append(edge)
        else:
            right_edges.append(edge)

    if len(left_edges) != len(right_edges):
        print("impossible")
        return

    left_edges.sort(key=lambda x: x[0])
    right_edges.sort(key=lambda x: x[1])

    result = []
    i = 0
    j = 0
    while i < len(left_edges) and j < len(right_edges):
        if left_edges[i][0] < right_edges[j][1]:
            result.append('L')
            i += 1
        elif left_edges[i][0] > right_edges[j][1]:
            result.append('R')
            j += 1
        else:
            print("impossible")
            return

    while i < len(left_edges):
        result.append('L')
        i += 1

    while j < len(right_edges):
        result.append('R')
        j += 1

    print(''.join(result))

