def solve():
    n, q = map(int, input().split())
    a = list(map(int, input().split()))

    def get_optimal_position(i):
        if i == 0:
            return 0

        left = a[i-1]
        right = a[i]

        if (left + right) % 2 == 0:
            return i - 1
        else:
            if (left - right) % 2 == 0:
                return i - 1
            else:
                if left > right:
                    return i - 1
                else:
                    return i

    for _ in range(q):
        i, x = map(int, input().split())
        a[i] = x

        optimal_position = get_optimal_position(i)

        print(optimal_position)

