def solve():
    n, m = map(int, input().split())
    v = []
    for _ in range(n):
        v.append(input())

    def count_changes(v):
        count = 0
        for i in range(m):
            digits = [int(x) for x in v[i]]
            digits.sort()
            for j in range(m):
                if digits[j] != int(v[i][j]):
                    count += 1
                    break
        return count

    def change_digit(v, i, digit):
        v[i] = str(digit) + v[i][i+1:]

    def sort(v):
        v.sort()
        return v

    min_changes = float('inf')
    best_v = None
    for i in range(1 << m):
        new_v = []
        for j in range(n):
            new_v.append(v[j])
            for k in range(m):
                if (i >> k) & 1:
                    change_digit(new_v[-1], k, str(int(new_v[-1][k]) + 1) % 10)
        changes = count_changes(new_v)
        if changes < min_changes:
            min_changes = changes
            best_v = new_v
        elif changes == min_changes:
            if best_v is None:
                best_v = new_v
            else:
                for j in range(n):
                    if best_v[j] != new_v[j]:
                        break
                else:
                    best_v = new_v

    print(*sort(best_v))

