    s, c, k = map(int, input().split())
    d = list(map(int, input().split()))

    def find_color_group(d, k):
        if not d:
            return 0

        d.sort()
        group_size = 1
        for i in range(1, len(d)):
            if d[i] - d[i-1] <= k:
                group_size += 1
            else:
                break

        return group_size

    machines_needed = 1
    current_group_size = find_color_group(d, k)

    while current_group_size * machines_needed < s:
        machines_needed += 1
        current_group_size = find_color_group(d, k)

    print(machines_needed)

