        while i < len(s):
            if s[i].isdigit():
                j = i
                while j < len(s) and s[j].isdigit():
                    j += 1
                atom = s[i:j]
                count[atom] = count.get(atom, 0) + int(s[j])
                i = j
            else:
                count[s[i]] = count.get(s[i], 0) + 1
                i += 1
        return count

    count1 = count_atoms(s1)
    count2 = count_atoms(s2)

    def can_make(s):
        count = {}
        for atom in s:
            if atom in count:
                count[atom] += 1
            else:
                count[atom] = 1
        for atom in count:
            if atom not in count1 or count1[atom] < count[atom]:
                return False
        return True

    n = 0
    for i in range(1 << len(s2)):
        temp = ""
        for j in range(len(s2)):
            if (i >> j) & 1:
                temp += s2[j]
        if can_make(temp):
            n += 1

    print(n)

