def solve():
    n, x = map(int, input().split())
    a = list(map(int, input().split()))

    if n == 2:
        if a[0] + a[1] <= 2 * x:
            print(0)
        else:
            print(1)
        return

    if n == 3:
        if a[0] + a[1] <= 2 * x and a[1] + a[2] <= 2 * x:
            print(0)
        elif a[0] + a[1] <= 2 * x and a[1] + a[2] <= 2 * x and a[0] + a[2] <= 2 * x:
            print(1)
        else:
            print(2)
        return

    dp = [[0] * (2 * x + 1) for _ in range(n)]

    for i in range(n):
