    for i in range(1, n):
        for j in range(2 * x + 1):
            if dp[i-1][j] > 0:
                dp[i][j] = dp[i-1][j] + 1
            if j + a[i] <= 2 * x:
                dp[i][j+a[i]] = max(dp[i][j+a[i]], dp[i-1][j] + 1)

    print(dp[n-1].index(max(dp[n-1])))

