def solve():
    n, m, k = map(int, input().split())
    a = []
    for _ in range(n):
        a.append(list(map(int, input().split())))

    def find_paths(i, j, xor_sum, memo):
        if (i, j, xor_sum) in memo:
            return memo[(i, j, xor_sum)]

        if i == n - 1 and j == m - 1:
            if xor_sum == k:
                return 1
            else:
                return 0

        if i == n - 1:
            return find_paths(i, j + 1, xor_sum ^ a[i][j], memo)
        if j == m - 1:
            return find_paths(i + 1, j, xor_sum ^ a[i][j], memo)

        count = 0
        count += find_paths(i, j + 1, xor_sum ^ a[i][j], memo)
        count += find_paths(i + 1, j, xor_sum ^ a[i][j], memo)

        memo[(i, j, xor_sum)] = count
        return count

    print(find_paths(0, 0, 0, {}))

solve()

def solve():
    n, m = map(int, input().split())
    edges = []
    for i in range(m):
        u, v = map(int, input().split())
        edges.append((u - 1, v - 1))

    def find_cycle(edges):
        graph = [[] for _ in range(n)]
        for u, v in edges:
            graph[u].append(v)
            graph[v].append(u)

        visited = [0] * n
        stack = []
        for i in range(n):
            if visited[i] == 0:
                if not dfs(i, graph, visited, stack):
                    return True
        return False

    def dfs(node, graph, visited, stack):
        visited[node] = 1
        stack.append(node)
        for neighbor in graph[node]:
            if visited[neighbor] == 0:
                if dfs(neighbor, graph, visited, stack):
                    return True
            elif neighbor in stack:
                return True
        stack.pop()
        visited[node] = 2
        return False

    if find_cycle(edges):
        print("NO")
    else:
        print("YES")
        orientation = ["0"] * m
        for i in range(m):
            u, v = edges[i]
            if u == v:
                orientation[i] = "1"
            else:
                orientation[i] = "0"
        print("".join(orientation))

solve()

