def find_performance(R, G):
    """
    Find the performance required to achieve the desired rating.

    Args:
        R (int): The current rating.
        G (int): The desired rating.

    Returns:
        int: The performance required to achieve the desired rating.
    """
    while True:
        average = (R + G) / 2
        if average == G:
            return int(G)
        elif average < G:
            R = average
        else:
            G = average

