def solve():
    n, k = map(int, input().split())
    s = input()

    def can_make(k):
        count = {}
        for char in s:
            count[char] = count.get(char, 0) + 1

        for char in count:
            if count[char] > k:
                return False

        return True

    def find_min_cost(k):
        if k == 0:
            return 0

        if can_make(k):
            return 0

        min_cost = float('inf')

        for i in range(n):
            remaining_k = k
            remaining_s = s[i:]

            cost = find_min_cost(remaining_k)

            if cost != -1:
                new_cost = cost + (n - len(s[:i]) + 1)

                if new_cost < min_cost:
                    min_cost = new_cost

        if min_cost == float('inf'):
            return -1

        return min_cost

    result = find_min_cost(k)
    print(result)

solve()

