    def rotate(arr):
        return arr[-1:] + arr[:-1]

    def find_rotation(arr, target):
        for i in range(len(arr) - 3 + 1):
            if arr[i:i+3] == target:
                return i
        return -1

    def check_sortable(arr, target_arr):
        for i in range(len(arr) - 2):
            current_arr = arr[i:i+3]
            target = target_arr[i:i+3]

            if current_arr == target:
                continue

            rotation_index = find_rotation(current_arr, target)
            if rotation_index != -1:
                new_arr = current_arr[rotation_index:] + current_arr[:rotation_index]
                if new_arr == target:
                    return True
        return False

    if check_sortable(a, b):
        print("Possible")
    else:
        print("Impossible")

