import math

def dist(p1, p2):
    return math.sqrt((p1[0] - p2[0])**2 + (p1[1] - p2[1])**2)

def angle(p1, p2):
    return math.acos((p1[0]*p2[0] + p1[1]*p2[1]) / (math.sqrt(p1[0]**2 + p1[1]**2) * math.sqrt(p2[0]**2 + p2[1]**2)))

def get_new_droplet(p1, p2, v1, v2):
    area1 = math.pi * p1[2]**2
    area2 = math.pi * p2[2]**2
    area_new = area1 + area2
    x_new = (area1 * p1[0] + area2 * p2[0]) / area_new
    y_new = (area1 * p1[1] + area2 * p2[1]) / area_new
    v_x_new = (area1 * v1[0] + area2 * v2[0]) / area_new
    v_y_new = (area1 * v1[1] + area2 * v2[1]) / area_new
    return [x_new, y_new, v_x_new, v_y_new, math.sqrt(v_x_new**2 + v_y_new**2)]

def get_new_droplets(droplets):
    new_droplets = []
    for i in range(len(droplets)):
        for j in range(i+1, len(droplets)):
            if dist(droplets[i], droplets[j]) < 2 * (droplets[i][2] + droplets[j][2]) + 0.001:
                new_droplets.append(get_new_droplet(droplets[i], droplets[j], [droplets[i][3], droplets[i][4]], [droplets[j][3], droplets[j][4]]))
    return new_droplets

