    n, k = map(int, input().split())

    if k > n // 2:
        k = n - k

    ans = 0

    for i in range(k + 1):
        ans = (ans + comb(n, i) * pow(i, k, 1000000007) * pow(n - i, n - k, 1000000007)) % 1000000007

    print(ans)

def comb(n, k):
    if k < 0 or k > n:
        return 0
    if k == 0 or k == n:
        return 1
    if k > n // 2:
        k = n - k
    res = 1
    for i in range(k):
        res = (res * (n - i)) % 1000000007
        res = (res * pow(i + 1, 1000000005, 1000000007)) % 1000000007
    return res

