def calculate_spaciousness(half1, half2):
    n1 = len(half1)
    n2 = len(half2)

    max_spaciousness = 0.0

    for i in range(n1):
        for j in range(n2):
            overlap = calculate_overlap(half1[i], half1[(i + 1) % n1], half2[j])
            if overlap > 0:
                max_spaciousness = max(max_spaciousness, calculate_area(half1[i], half1[(i + 1) % n1], half2[j]) - overlap)

    return max_spaciousness

def calculate_overlap(r1, r2, r3):
    d = math.sqrt((r2[0] - r3[0])**2 + (r2[1] - r3[1])**2)
    return max(0, d - r1[2] - r3[2])

def calculate_area(r1, r2, r3):
    return (r1[2] * r1[2] + r2[2] * r2[2] + r3[2] * r3[2]) / 2

