    if start is None or end is None:
        print("INCONCEIVABLE")
        return

    def is_valid(x, y):
        return 0 <= x < n and 0 <= y < n and grid[x][y] != '#'

    def count_paths(start, end):
        dp = [[0] * n for _ in range(n)]
        dp[start[0]][start[1]] = 1

        directions = [(0, 1), (1, 0)]  # Right, Down

        for i in range(n):
            for j in range(n):
                if dp[i][j] != 0:
                    for dx, dy in directions:
                        nx, ny = i + dx, j + dy
                        if is_valid(nx, ny):
                            dp[nx][ny] = (dp[nx][ny] + dp[i][j]) % (2**31 - 1)

        return dp[end[0]][end[1]]

    def count_paths_up_left(start, end):
        dp = [[0] * n for _ in range(n)]
        dp[start[0]][start[1]] = 1

        directions = [(-1, 0), (0, -1)]  # Up, Left

        for i in range(n):
            for j in range(n):
                if dp[i][j] != 0:
                    for dx, dy in directions:
                        nx, ny = i + dx, j + dy
                        if 0 <= nx < n and 0 <= ny < n and grid[nx][ny] != '#':
                            dp[nx][ny] = (dp[nx][ny] + dp[i][j]) % (2**31 - 1)

        return dp[end[0]][end[1]]

    paths_right_down = count_paths(start, end)
    paths_up_left = count_paths_up_left(start, end)

    if paths_right_down == 0 and paths_up_left != 0:
