def solve():
    n, m = map(int, input().split())
    edges = []
    for _ in range(m):
        u, v, weight = map(int, input().split())
        edges.append((u, v, weight))

    def find_min_range():
        adj = [[] for _ in range(n)]
        for u, v, weight in edges:
            adj[u].append((v, weight))
            adj[v].append((u, weight))

        dist = [[float('inf')] * n for _ in range(n)]
        for i in range(n):
            dist[i][i] = 0

        for u, v, weight in edges:
            dist[u][v] = weight
            dist[v][u] = weight

        for k in range(n):
            for i in range(n):
                for j in range(n):
                    dist[i][j] = min(dist[i][j], dist[i][k] + dist[k][j])

        for i in range(n):
            for j in range(n):
                if dist[i][j] == float('inf'):
                    return float('inf')

        return max(dist[i][j] for i in range(n) for j in range(n) if i != j)

    min_range = find_min_range()
    if min_range == float('inf'):
        print("IMPOSSIBLE")
    else:
        print(min_range)

