        changes = 0
        for r in range(n):
            for c in range(m):
                if (r + c) % 2 == 0:
                    count = 0
                    for i in range(r, r + 2):
                        for j in range(c, c + 2):
                            if i < 0 or i >= n or j < 0 or j >= m:
                                continue
                            if mat[i][j] == '1':
                                count += 1
                    if count % 2 == 0:
                        changes += 1
        return changes

    def find_min_changes():
        best_changes = float('inf')
        for r1 in range(n):
            for r2 in range(r1, n):
                for c1 in range(m):
