def solve():
    n = int(input())
    a = list(map(int, input().split()))
    b = input()

    def check(l, r):
        b_new = [0] * n
        for i in range(n):
            if a[i] >= l and a[i] <= r:
                b_new[i] = 1
            elif a[i] < l or a[i] > r:
                b_new[i] = 0
            else:
                valid = True
                for j in range(1, 5):
                    if a[i] - j < l:
                        b_new[i] = 0
                        valid = False
                        break
                if valid:
                    b_new[i] = 1
        return b_new

    def find_l_r():
        low = -10**9
        high = 10**9

        while high - low > 1:
            mid1 = low + (high - low) // 3
            mid2 = high - (high - low) // 3

            b1 = check(low, mid1)
            b2 = check(mid1 + 1, mid2)
            b3 = check(mid2 + 1, high)

            if b1 == b and b2 == b and b3 == b:
                return low, high
            elif b1 == b:
                high = mid2
            elif b2 == b:
                low = mid1 + 1
            else:
                low = mid1 + 1
                high = mid2

        b1 = check(low, high)
        b2 = check(low + 1, high)
        if b1 == b and b2 == b:
            return low, high
        elif b1 == b:
            return low, high
        else:
            return low + 1, high + 1

    b = check(-10**9, 10**9)
    l, r = find_l_r()
    print(l, r)

