def solve():
    n, q = map(int, input().split())
    a = list(map(int, input().split()))

    def replace(x, k):
        a[x - 1] = k

    def reverse(k):
        start = (k - 1) * (1 << k)
        end = (k * 2) * (1 << k) - 1
        for i in range((end - start + 1) // 2):
            a[start + i], a[end - i] = a[end - i], a[start + i]

    def swap(k):
        start1 = (2 * k - 1) * (1 << k)
        start2 = (2 * k) * (1 << k) - 1
        for i in range((start2 - start1 + 1) // 2):
            a[start1 + i], a[start2 - i] = a[start2 - i], a[start1 + i]
            a[start1 + i + 1], a[start2 - i - 1] = a[start2 - i - 1], a[start1 + i + 1]

    def sum_query(l, r):
        start = (l - 1) * (1 << (r - l + 1).bit_length())
        end = (r * 2) * (1 << (r - l + 1).bit_length()) - 1
        return sum(a[start:end + 1])

    for _ in range(q):
        query = input().split()
        if query[0] == '1':
            x, k = map(int, query[1:])
            replace(x, k)
        elif query[0] == '2':
            k = int(query[1])
            reverse(k)
        elif query[0] == '3':
            k = int(query[1])
            swap(k)
        else:
            l, r = map(int, query[1:])
            print(sum_query(l, r))

solve()

