        return False

    def can_move_down(pawn, board):
        r, c = pawn
        if r < 7 and board[r+1][c] == '.':
            return True
        return False

    def calculate_moves(player_pawns, board):
        moves = 0
        for pawn in player_pawns:
            if can_move_up(pawn, board):
                moves += 1
            if can_move_down(pawn, board):
                moves += 1
        return moves

    a_moves = calculate_moves(white_pawns, board)
    b_moves = calculate_moves(black_pawns, board)

    if a_moves > b_moves:
        print("A")
    else:
        print("B")

