def solve():
    n, k = map(int, input().split())

    def steps(num, div):
        if num == 0:
            return 0

        if num % div == 0:
            return 1 + steps(num // div, div)
        else:
            return float('inf')

    def calculate_steps(num):
        if num == 0:
            return 0

        min_steps = float('inf')

        # Option 1: Decrease by 1
        steps_decrease = 1 + calculate_steps(num - 1)

        # Option 2: Divide by k if divisible
        if num % k == 0:
            steps_divide = 1 + calculate_steps(num // k)
            min_steps = min(min_steps, steps_decrease, steps_divide)
        else:
            min_steps = min(min_steps, steps_decrease)

        return min_steps

    print(calculate_steps(n))

