def solve():
    n, A, B, C, T = map(int, input().split())
    t = list(map(int, input().split()))

    max_amount = 0

    for i in range(1 << n):
        unread_messages = 0
        unread_times = []

        for j in range(n):
            if (i >> j) & 1:
                unread_messages += 1
                unread_times.append(t[j])

        total_amount = A * unread_messages

        for j in range(unread_messages):
            total_amount += C * unread_messages - B * (unread_times[j] - 1)

        if total_amount > max_amount:
            max_amount = total_amount

        if max(unread_times) > T:
            continue

        for j in range(n):
            if (i >> j) & 1:
                for k in range(j + 1, n):
                    if (i >> k) & 1:
                        if max(unread_times) + t[k] <= T:
                            temp_unread_messages = unread_messages - 1
                            temp_unread_times = [x for x in unread_times if x != t[k]]

                            temp_total_amount = total_amount - A + A - B * (max(unread_times) - 1) + A - B * (max(temp_unread_times) - 1)

                            for l in range(temp_unread_messages):
                                temp_total_amount += C * temp_unread_messages - B * (temp_unread_times[l] - 1)

                            if temp_total_amount > max_amount:
                                max_amount = temp_total_amount

    print(max_amount)

solve()

