def solve():
    n, m, k = map(int, input().split())
    maze = [input() for _ in range(n)]
    start_row = -1
    start_col = -1
    for i in range(n):
        for j in range(m):
            if maze[i][j] == 'X':
                start_row = i
                start_col = j
                break
        if start_row != -1:
            break

    ways = []

    def generate_ways(row, col, path, steps_left):
        if steps_left == 0:
            ways.append(path)
            return

        directions = [(0, 1), (0, -1), (1, 0), (-1, 0)]  # R, L, D, U

        for dr, dc in directions:
            new_row = row + dr
            new_col = col + dc

            if 0 <= new_row < n and 0 <= new_col < m:
                if maze[new_row][new_col] == '.':
                    generate_ways(new_row, new_col, path + maze[new_row][new_col], steps_left - 1)

    generate_ways(start_row, start_col, "", k)

    if not ways:
        print("IMPOSSIBLE")
        return

    min_way = min(ways)

    print(min_way)

solve()

