def solve():
    s = input()
    t = input()

    def build_suffix_automaton(s):
        transitions = {}
        end_counts = {}
        start_states = {}

        start_states[''] = 0

        for i in range(len(s)):
            curr = ''
            for j in range(i, len(s)):
                curr += s[j]

                if curr not in transitions:
                    transitions[curr] = {}
                    end_counts[curr] = 0
                    start_states[curr] = 0

                start_states[curr] += 1
                end_counts[curr] += 1

                curr = curr[:-1]

        return transitions, end_counts, start_states

    def build_suffix_array(s):
        sa = list(range(len(s)))
        sa.sort(key=lambda x: s[x])

        l = 1
        while l < len(s):
            new_sa = [-1] * len(s)
            counts = {}

            for i in range(len(s)):
                counts[sa[i]] = i

            next_counts = {}
            for i in range(len(s)):
                next_counts[sa[i]] = counts[sa[i]] + 1 if i < len(s) - 1 else -1

            next_sa = [-1] * len(s)
            for i in range(len(s)):
                rank = next_counts[sa[i]]
                if rank != -1:
                    next_sa[rank] = i

            sa = next_sa
            l *= 2

        return sa

    def transform_with_automaton(s, t, transitions, end_counts, start_states):
        curr = ''
        for char in s:
            curr += char
            if curr in transitions and end_counts[curr] > 0:
                end_counts[curr] -= 1
            else:
                return False

        return True

    def transform_with_array(s, t, sa):
        curr = ''
        for i in range(len(sa)):
            curr += s[sa[i]]

        if curr == t:
            return True

        return False

    transitions, end_counts, start_states = build_suffix_automaton(s)

    sa = build_suffix_array(s)

    if transform_with_automaton(s, t, transitions, end_counts, start_states):
        if transform_with_array(s, t, sa):
            print("both")
        else:
            print("automaton")
    else:
        if transform_with_array(s, t, sa):
            print("array")
        else:
            print("need tree")

solve()

