def solve():
    n, m = map(int, input().split())
    edges = []
    for _ in range(m):
        u, v = map(int, input().split())
        edges.append((u, v))

    def find(parents, i):
        if parents[i] == i:
            return i
        parents[i] = find(parents, parents[i])
        return parents[i]

    def union(parents, ranks, i, j):
        i_root = find(parents, i)
        j_root = find(parents, j)
        if i_root == j_root:
            return
        if ranks[i_root] < ranks[j_root]:
            parents[i_root] = j_root
        elif ranks[i_root] > ranks[j_root]:
            parents[j_root] = i_root
        else:
            parents[j_root] = i_root
            ranks[i_root] += 1

    parents = list(range(n + 1))
    ranks = [0] * (n + 1)

    for u, v in edges:
        union(parents, ranks, u, v)

    groups = set()
    for i in range(1, n + 1):
        groups.add(find(parents, i))

    print(len(groups))

solve()

