def solve():
    s = input()
    t = input()

    count = 0
    n = len(t)

    for i in range(n):
        left = t[:i+1]
        right = t[i+1:]

        if len(left) > 0 and len(right) > 0:
            if left == s and right == s:
                count += 1
            elif left == s:
                count += solve_recursive(right, s)
            elif right == s:
                count += solve_recursive(left, s)

    return count

def solve_recursive(s, t):
    if len(t) == 0:
        return 0

    count = 0
    n = len(t)

