def solve():
    n, k = map(int, input().split())
    w = list(map(int, input().split()))

    MOD = 10**9 + 7

    def combinations(n, k):
        if k < 0 or k > n:
            return 0
        if k == 0 or k == n:
            return 1
        if k > n // 2:
            k = n - k

        res = 1
        for i in range(k):
            res = (res * (n - i)) % MOD
            res = (res * pow(i + 1, MOD - 2, MOD)) % MOD
        return res

    def calculate_partitions(weights, k):
        n = len(weights)

        if k == 0:
            return 0

        if k == 1:
            return sum(weights) % MOD

        if k > n:
            return 0

        ans = 0

        for i in range(1, n + 1):
            if k == i:
                ans = (ans + sum(weights[:i]) * combinations(n, i)) % MOD
                continue

            for subset in combinations(list(range(n)), i):
                subset_weights = []
                subset_sum = 0
                for j in range(n):
                    if j in subset:
                        subset_weights.append(weights[j])
                        subset_sum = (subset_sum + weights[j]) % MOD

                if len(subset_weights) == k:
                    ans = (ans + sum(subset_weights) * combinations(n, len(subset))) % MOD

        return ans

    ans = calculate_partitions(w, k)
    print(ans)

